package com.getmicropad.NPXParser;

import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.util.ArrayList;
import java.util.Date;
import java.util.List;
import org.junit.Before;
import org.junit.Test;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertTrue;

public class Tests {
	private Notepad notepad;

	@Before
	public void setUp() throws Exception {
		notepad = new Notepad("Test");
		XMLGregorianCalendarTransform transform = new XMLGregorianCalendarTransform();
		notepad.setLastModified(transform.read("2017-12-27T23:51:45.059+13:00"));

		Section s1 = new Section("test section");
		Note n1 = new Note("Blarghh");
		n1.setTime(transform.read("2017-12-27T23:51:45.075+13:00"));
		n1.setParent(s1);
		List<String> addonList = new ArrayList<>();
		addonList.add("asciimath");
		addonList.add("test");
		n1.setAddons(addonList);
		n1.bibliography.add(new Source(1, "markdown1", "https://example.com"));
		Note n2 = new Note("More");
		n2.setTime(transform.read("2017-12-27T23:51:45.075+13:00"));
		n2.setParent(s1);
		BinaryElement be = new ImageElement("data:image/gif;base64,", "image1", "10px", "10px", "400px", "400px");
		n2.elements.add(be);
		n2.elements.add(new MarkdownElement("This #pie is some\n\nescaped text", "markdown1", "10px", "10px", "400px", "400px", "16px"));
		s1.notes.add(n1);
		s1.notes.add(n2);
		notepad.sections.add(s1);
		Section s2 = new Section("More");
		s2.sections.add(new Section("Inception"));
		notepad.sections.add(s2);
	}

	@Test
	public void testNotepadCreated() throws Exception {
		String expected = new String(Files.readAllBytes(Paths.get("Test.npx")));
		assertEquals(expected, Parser.toXml(notepad));
	}

	@Test
	public void testParseNpx() throws Exception {
		notepad = Parser.parseNpx(new File("Example Notepad.npx"));
		assertEquals(notepad.getTitle(), "Example Notepad");
	}

	@Test
	public void testSearch() {
		NotepadSearcher notepadSearcher = new NotepadSearcher(notepad);

		List<Note> mock = new ArrayList<>();
		mock.add(notepad.getSections().get(0).notes.get(1));

		assertEquals("Title search should work", mock, notepadSearcher.search("Mo"));

		assertEquals("Hashtag search should work", mock, notepadSearcher.search("#pie"));
	}
}
